import Foundation
import os.log

class FetchInAppMessageURLSessionManager {
    
    let inAppMessageGetter = InAppMessageGetter()
    
    func completionHandler(inAppMessageURLSessionData: InAppMessageURLSessionData, httpResponse: HTTPURLResponse, location: URL) {
        do {
            let responseBody = try String(contentsOfFile: location.path)
            
            switch httpResponse.statusCode {
            case 200:
                break
            case 401:
                let message = "Status code: \(httpResponse.statusCode). Description: \(HTTPURLResponse.localizedString(forStatusCode: httpResponse.statusCode))"
                let responseError = ResponseError(message: message, statusCode: httpResponse.statusCode, responseBody: responseBody, systemError: nil)
                self.inAppMessageGetter.systemErrorHandler(mcID: inAppMessageURLSessionData.mcID, error: responseError)
                
                SDKSecurity.shared.updateJWT()
            case 500: // tmp logic
                let html = "<html>" +
                    "<head>" +
                    "<style>" +
                    "body { font-size: 16px; }" +
                    ".popup { padding: 8px; border-color: rgba(120, 130, 140, 0.13); background-color: #fff; background-clip: padding-box; color: #555; height: 100%; width: 100%; display: flex; flex-direction: column; align-items: center; box-sizing: border-box; font-family: 'HelveticaNeue','Helvetica Neue',Helvetica,Arial,sans-serif; }" +
                    ".title { text-align: center; font-size: 3rem; line-height: 1.4; font-weight: bold; }" +
                    ".subtitle { font-size: 2rem; font-weight: normal; line-height: 1.4; text-align: center; }" +
                    ".discount { font-size: 5rem; font-weight: bold; text-align: center; color: #f44336; margin-top: 4rem; }" +
                    ".product { height: 100%; display: flex; flex-direction: column; }" +
                    ".image { margin-top: 4rem; height: 60%; width: auto; object-fit: contain; text-align: center; }" +
                    ".image > img { height: 100%; width: auto; }" +
                    ".actions { margin-top: auto; font-size: 2rem; }" +
                    ".checkout-btn { padding: 16px 16px; text-decoration: none; text-align: center; vertical-align: middle; white-space: nowrap; cursor: pointer; border: 1px solid transparent; user-select: none; border-radius: 2px; background-color: #f45b4f; color: #fff; font-size: 4rem; margin-left: 5rem; margin-right: 5rem; }" +
                    ".discard { margin-top: 3rem; text-align: center; margin-bottom: 2rem; }" +
                    "</style>" +
                    "</head>" +
                    "<body>" +
                    "<div class='popup'>" +
                    "<div class='product'>" +
                    "<div class='image'>" +
                    "<img src='data:image/jpeg;base64,' />" +
                    "</div>" +
                    "<div class='title'>Alix Long Sleeve Woven Shirt</div>" +
                    "<div class='subtitle'>Robert Graham</div>" +
                    "<div class='discount'>%50 Off this weekend!</div>" +
                    "<div class='actions'>" +
                    "<div class='checkout-btn' onclick=\"action('https://tjs.cordialdev.com/prep-tj1.html', 'in_app_message_event_name')\">Check It Out</div>" +
                    "<div class='discard' onclick='action()'>Discard</div>" +
                    "</div>" +
                    "</div>" +
                    "</div>" +
                    "</body>" +
                "</html>"
                
                var top = 15
                var right = 10
                var bottom = 20
                var left = 10
                
                var type: InAppMessageType
                var displayType: InAppMessageDisplayType
                let expirationTime = "2020-07-25T13:58:01Z"
                
                switch inAppMessageURLSessionData.mcID {
                case "test_modal":
                    type = InAppMessageType.modal
                    displayType = InAppMessageDisplayType.displayOnAppOpenEvent
                case "test_fullscreen":
                    type = InAppMessageType.fullscreen
                    displayType = InAppMessageDisplayType.displayOnAppOpenEvent
                    
                    top = 0
                    right = 0
                    bottom = 0
                    left = 0
                case "test_banner_up":
                    type = InAppMessageType.banner_up
                    displayType = InAppMessageDisplayType.displayImmediately
                    
                    let height = 20
                    
                    top = 5
                    right = 5
                    bottom = Int(100 - Double(height) / 100.0 * 100)
                    left = 5
                case "test_banner_bottom":
                    type = InAppMessageType.banner_bottom
                    displayType = InAppMessageDisplayType.displayImmediately
                    
                    let height = 20
                    
                    top = Int(100 - Double(height) / 100.0 * 100)
                    right = 5
                    bottom = 5
                    left = 5
                default:
                    type = InAppMessageType.modal
                    displayType = InAppMessageDisplayType.displayOnAppOpenEvent
                }
                
                let inAppMessageData = InAppMessageData(mcID: inAppMessageURLSessionData.mcID, html: html, type: type, displayType: displayType, top: top, right: right, bottom: bottom, left: left, expirationTime: InternalCordialAPI().getDateFromTimestamp(timestamp: expirationTime))
                
                self.inAppMessageGetter.completionHandler(inAppMessageData: inAppMessageData)
            default:
                let message = "Status code: \(httpResponse.statusCode). Description: \(HTTPURLResponse.localizedString(forStatusCode: httpResponse.statusCode))"
                let responseError = ResponseError(message: message, statusCode: httpResponse.statusCode, responseBody: responseBody, systemError: nil)
                self.inAppMessageGetter.logicErrorHandler(error: responseError)
            }
        } catch let error {
            if CordialApiConfiguration.shared.osLogManager.isAvailableOsLogLevelForPrint(osLogLevel: .error) {
                os_log("Failed decode response data. Error: [%{public}@]", log: OSLog.cordialInAppMessage, type: .error, error.localizedDescription)
            }
        }
    }
    
    func errorHandler(inAppMessageURLSessionData: InAppMessageURLSessionData, error: Error) {
        let responseError = ResponseError(message: error.localizedDescription, statusCode: nil, responseBody: nil, systemError: error)
        self.inAppMessageGetter.systemErrorHandler(mcID: inAppMessageURLSessionData.mcID, error: responseError)
    }

}
